const promesa = new Promise((resolve, reject) => {
    // resolve callback que se ejectua cuando la promesa se resuelve correctamente
    // reject lo llamamos cuando hay un error en la promesa
    setTimeout(() => {
        console.log('2 segundo después');
    }, 2000);
});
