function saludar(nombre) {
    return `Hola ${nombre}!`;
}

console.log(saludar('Goku'));

// Es mejor crear funciones de la siguiente manera
const saludar2 =  function(nombre) {
    return `Hola desde saludar2(): ${nombre}.`;
}

console.log(saludar2('Ezequiel'));
