const persona = {
    // Los objetos trabajan con pares de valores
    nombre: 'Tony',
    apellido: 'Stark',
    edad: '45',
}

// es lo mismo que {persona:persona}
console.log({persona});

// Para clonar objetos:
const persona2 = {...persona};
console.log(persona2);
