// Asignación Desestructurante
const persona = {
    nombre: 'Tony',
    edad: 45,
    clave: 'Ironman',
}

console.log(persona.nombre);
console.log(persona.edad);
console.log(persona.clave);

const {nombre, edad, clave} = persona;

console.log(nombre);
console.log(edad);
console.log(clave);

const retornaPersona = ({nombre, edad, clave, rango = 'capitan'}) => {
    return {
        nombreClave: clave,
        anios: edad,
    }
}

retornaPersona(persona);

const useContext2 = ({nombre, edad, clave, rango = 'capitan'}) => {
    return {
        nombreClave: clave,
        anios: edad,
    }
}

useContext2(persona);
